  `product_id` int(10) unsigned DEFAULT '0' COMMENT '商品id',
  `group_id` int(10) unsigned DEFAULT '0' COMMENT '集团id',
  `store_id` int(10) unsigned DEFAULT '0' COMMENT '店铺id',
