DROP TABLE IF EXISTS rt_manger;
CREATE TABLE `rt_manger` (
  `manger_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '唯一标志',
  `username` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户名',
